sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   //Type appropriate comment here, and begin script below
    if(g_form.getValue('end_date') == '')
        return;  // Exit early if 'end_date' is empty.

    if(g_form.getValue('end_date') <g_form.getValue('start_date')) {
g_form.clearMessages();  // function ensures that any previous messages (such as old error messages) are cleared before the new error message is added.

g_form.addErrorMessage(g_form.getLabelOf('end_date') + ' ' 
	+ getMessage('must be after') + ' ' + g_form.getLabelOf('start_date')); //Gets the label (display name) of a field.

        return false; // Stop the form submission and prevent the invalid data from being saved.
    }

}
